import React, { useState } from 'react';
import { Course, Teacher, Classroom } from '../types';
import { Plus, Edit2, Trash2, BookOpen, Upload } from 'lucide-react';
import { CourseImporter } from './CourseImporter';

interface CourseManagerProps {
  courses: Course[];
  teachers: Teacher[];
  classrooms: Classroom[];
  onAddCourse: (course: Omit<Course, 'id'>) => void;
  onUpdateCourse: (id: string, course: Partial<Course>) => void;
  onDeleteCourse: (id: string) => void;
}

export const CourseManager: React.FC<CourseManagerProps> = ({
  courses,
  teachers,
  classrooms,
  onAddCourse,
  onUpdateCourse,
  onDeleteCourse
}) => {
  const [isAddingCourse, setIsAddingCourse] = useState(false);
  const [editingCourse, setEditingCourse] = useState<string | null>(null);
  const [showImporter, setShowImporter] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    subject: '',
    teacherId: '',
    classroomId: '',
    duration: '1',
    frequency: '1',
    studentCount: '',
    classroomType: '',
    equipment: '',
    consecutiveSlots: false
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const courseData = {
      name: formData.name,
      subject: formData.subject,
      teacherId: formData.teacherId,
      classroomId: formData.classroomId || undefined,
      duration: parseInt(formData.duration),
      frequency: parseInt(formData.frequency),
      studentCount: parseInt(formData.studentCount),
      requirements: {
        classroomType: formData.classroomType as Classroom['type'] || undefined,
        equipment: formData.equipment ? formData.equipment.split(',').map(s => s.trim()).filter(s => s) : undefined,
        consecutiveSlots: formData.consecutiveSlots
      }
    };

    if (editingCourse) {
      onUpdateCourse(editingCourse, courseData);
      setEditingCourse(null);
    } else {
      onAddCourse(courseData);
      setIsAddingCourse(false);
    }

    setFormData({
      name: '',
      subject: '',
      teacherId: '',
      classroomId: '',
      duration: '1',
      frequency: '1',
      studentCount: '',
      classroomType: '',
      equipment: '',
      consecutiveSlots: false
    });
  };

  const handleEdit = (course: Course) => {
    setFormData({
      name: course.name,
      subject: course.subject,
      teacherId: course.teacherId,
      classroomId: course.classroomId || '',
      duration: course.duration.toString(),
      frequency: course.frequency.toString(),
      studentCount: course.studentCount.toString(),
      classroomType: course.requirements.classroomType || '',
      equipment: course.requirements.equipment?.join(', ') || '',
      consecutiveSlots: course.requirements.consecutiveSlots || false
    });
    setEditingCourse(course.id);
    setIsAddingCourse(true);
  };

  const handleCancel = () => {
    setIsAddingCourse(false);
    setEditingCourse(null);
    setFormData({
      name: '',
      subject: '',
      teacherId: '',
      classroomId: '',
      duration: '1',
      frequency: '1',
      studentCount: '',
      classroomType: '',
      equipment: '',
      consecutiveSlots: false
    });
  };

  const getTeacherName = (teacherId: string) => {
    return teachers.find(t => t.id === teacherId)?.name || '未知教师';
  };

  const getClassroomName = (classroomId?: string) => {
    if (!classroomId) return '未指定';
    return classrooms.find(c => c.id === classroomId)?.name || '未知教室';
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900 flex items-center gap-2">
          <BookOpen className="h-6 w-6" />
          课程管理
        </h2>
        <button
          onClick={() => setIsAddingCourse(true)}
          className="btn-primary flex items-center gap-2"
        >
          <Plus className="h-4 w-4" />
          添加课程
        </button>
      </div>

      {isAddingCourse && (
        <div className="card">
          <h3 className="text-lg font-semibold mb-4">
            {editingCourse ? '编辑课程' : '添加新课程'}
          </h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  课程名称 *
                </label>
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  className="input-field"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  学科 *
                </label>
                <input
                  type="text"
                  value={formData.subject}
                  onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                  className="input-field"
                  required
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  授课教师 *
                </label>
                <select
                  value={formData.teacherId}
                  onChange={(e) => setFormData({ ...formData, teacherId: e.target.value })}
                  className="input-field"
                  required
                >
                  <option value="">请选择教师</option>
                  {teachers.map((teacher) => (
                    <option key={teacher.id} value={teacher.id}>
                      {teacher.name} ({teacher.subjects.join(', ')})
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  指定教室
                </label>
                <select
                  value={formData.classroomId}
                  onChange={(e) => setFormData({ ...formData, classroomId: e.target.value })}
                  className="input-field"
                >
                  <option value="">自动分配</option>
                  {classrooms.map((classroom) => (
                    <option key={classroom.id} value={classroom.id}>
                      {classroom.name} (容量: {classroom.capacity})
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  课时长度 *
                </label>
                <select
                  value={formData.duration}
                  onChange={(e) => setFormData({ ...formData, duration: e.target.value })}
                  className="input-field"
                  required
                >
                  <option value="1">1节课</option>
                  <option value="2">2节课</option>
                  <option value="3">3节课</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  每周频次 *
                </label>
                <select
                  value={formData.frequency}
                  onChange={(e) => setFormData({ ...formData, frequency: e.target.value })}
                  className="input-field"
                  required
                >
                  <option value="1">1次/周</option>
                  <option value="2">2次/周</option>
                  <option value="3">3次/周</option>
                  <option value="4">4次/周</option>
                  <option value="5">5次/周</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  学生人数 *
                </label>
                <input
                  type="number"
                  value={formData.studentCount}
                  onChange={(e) => setFormData({ ...formData, studentCount: e.target.value })}
                  className="input-field"
                  min="1"
                  required
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  教室类型要求
                </label>
                <select
                  value={formData.classroomType}
                  onChange={(e) => setFormData({ ...formData, classroomType: e.target.value })}
                  className="input-field"
                >
                  <option value="">无特殊要求</option>
                  <option value="normal">普通教室</option>
                  <option value="lab">实验室</option>
                  <option value="multimedia">多媒体教室</option>
                  <option value="gym">体育馆</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  设备要求 (用逗号分隔)
                </label>
                <input
                  type="text"
                  value={formData.equipment}
                  onChange={(e) => setFormData({ ...formData, equipment: e.target.value })}
                  className="input-field"
                  placeholder="例如: 投影仪, 实验台"
                />
              </div>
            </div>

            <div>
              <label className="flex items-center gap-2">
                <input
                  type="checkbox"
                  checked={formData.consecutiveSlots}
                  onChange={(e) => setFormData({ ...formData, consecutiveSlots: e.target.checked })}
                  className="rounded"
                />
                <span className="text-sm font-medium text-gray-700">需要连续时间段</span>
              </label>
            </div>

            <div className="flex gap-2">
              <button type="submit" className="btn-primary">
                {editingCourse ? '更新' : '添加'}
              </button>
              <button type="button" onClick={handleCancel} className="btn-secondary">
                取消
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {courses.map((course) => (
          <div key={course.id} className="card">
            <div className="flex justify-between items-start mb-3">
              <h3 className="text-lg font-semibold text-gray-900">{course.name}</h3>
              <div className="flex gap-1">
                <button
                  onClick={() => handleEdit(course)}
                  className="p-1 text-gray-500 hover:text-primary-600"
                >
                  <Edit2 className="h-4 w-4" />
                </button>
                <button
                  onClick={() => onDeleteCourse(course.id)}
                  className="p-1 text-gray-500 hover:text-red-600"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            <div className="space-y-2 text-sm text-gray-600">
              <p>📚 学科: {course.subject}</p>
              <p>👨‍🏫 教师: {getTeacherName(course.teacherId)}</p>
              <p>🏫 教室: {getClassroomName(course.classroomId)}</p>
              <p>⏱️ {course.duration}节课 × {course.frequency}次/周</p>
              <p>👥 {course.studentCount}人</p>
              {course.requirements.consecutiveSlots && (
                <p className="text-orange-600">🔗 需要连续时间段</p>
              )}
            </div>
          </div>
        ))}
      </div>

      {courses.length === 0 && (
        <div className="text-center py-12 text-gray-500">
          <BookOpen className="h-12 w-12 mx-auto mb-4 opacity-50" />
          <p>暂无课程信息，点击上方按钮添加课程</p>
        </div>
      )}
    </div>
  );
};
