import React, { useState } from 'react';
import { Course, Teacher, Classroom } from '../types';
import { Upload, FileText, AlertCircle, CheckCircle } from 'lucide-react';

interface CourseImporterProps {
  teachers: Teacher[];
  classrooms: Classroom[];
  onImportCourses: (courses: Omit<Course, 'id'>[]) => void;
}

interface ParsedCourse {
  name: string;
  teacherName: string;
  college: string;
  campus: string;
  hours: number;
  credits: number;
  schedule: string;
  classroom?: string;
}

export const CourseImporter: React.FC<CourseImporterProps> = ({
  teachers,
  classrooms,
  onImportCourses
}) => {
  const [inputText, setInputText] = useState('');
  const [parsedCourses, setParsedCourses] = useState<ParsedCourse[]>([]);
  const [importResult, setImportResult] = useState<{
    success: boolean;
    message: string;
    details?: string[];
  } | null>(null);

  // 解析课程文本
  const parseCourseText = (text: string): ParsedCourse[] => {
    const lines = text.trim().split('\n').filter(line => line.trim());
    const courses: ParsedCourse[] = [];

    for (const line of lines) {
      try {
        // 解析格式：课程名\t教师名\t学院\t校区\t学时\t学分\t时间安排
        const parts = line.split('\t').map(part => part.trim());
        
        if (parts.length >= 6) {
          const [name, teacherName, college, campus, hoursStr, creditsStr, ...scheduleParts] = parts;
          const schedule = scheduleParts.join('\t');
          
          // 提取教室信息
          const classroomMatch = schedule.match(/\(([^)]+)\)/);
          const classroom = classroomMatch ? classroomMatch[1] : undefined;
          
          courses.push({
            name,
            teacherName,
            college,
            campus,
            hours: parseInt(hoursStr) || 0,
            credits: parseInt(creditsStr) || 0,
            schedule,
            classroom
          });
        }
      } catch (error) {
        console.warn('解析课程行失败:', line, error);
      }
    }

    return courses;
  };

  // 解析时间安排
  const parseSchedule = (schedule: string) => {
    // 提取周次和时间信息
    // 例如: "1-8周，星期一第7-8节 星期三第7-8节 (立人楼B406)"
    
    const weekMatch = schedule.match(/(\d+)-(\d+)周/);
    const dayTimeMatches = schedule.match(/星期([一二三四五六日])第(\d+)-?(\d+)?节/g);
    
    let frequency = 0;
    let duration = 2; // 默认2节课
    
    if (dayTimeMatches) {
      frequency = dayTimeMatches.length;
      
      // 计算课程时长
      const firstMatch = dayTimeMatches[0].match(/第(\d+)-?(\d+)?节/);
      if (firstMatch) {
        const start = parseInt(firstMatch[1]);
        const end = firstMatch[2] ? parseInt(firstMatch[2]) : start;
        duration = end - start + 1;
      }
    }
    
    return { frequency, duration };
  };

  // 查找或创建教师
  const findOrCreateTeacher = (teacherName: string, subject: string): string => {
    // 处理多个教师的情况
    const teacherNames = teacherName.split(',').map(name => name.trim());
    const primaryTeacher = teacherNames[0];
    
    const existingTeacher = teachers.find(t => t.name === primaryTeacher);
    if (existingTeacher) {
      return existingTeacher.id;
    }
    
    // 如果教师不存在，返回一个临时ID，实际应用中需要先创建教师
    return `temp_teacher_${Date.now()}_${Math.random()}`;
  };

  // 查找教室
  const findClassroom = (classroomName?: string): string | undefined => {
    if (!classroomName) return undefined;
    
    const classroom = classrooms.find(c => c.name === classroomName);
    return classroom?.id;
  };

  // 处理解析
  const handleParse = () => {
    if (!inputText.trim()) {
      setImportResult({
        success: false,
        message: '请输入课程信息'
      });
      return;
    }

    try {
      const parsed = parseCourseText(inputText);
      setParsedCourses(parsed);
      
      if (parsed.length === 0) {
        setImportResult({
          success: false,
          message: '未能解析出任何课程信息，请检查格式'
        });
      } else {
        setImportResult({
          success: true,
          message: `成功解析 ${parsed.length} 门课程`,
          details: parsed.map(c => c.name)
        });
      }
    } catch (error) {
      setImportResult({
        success: false,
        message: `解析失败: ${error instanceof Error ? error.message : '未知错误'}`
      });
    }
  };

  // 处理导入
  const handleImport = () => {
    if (parsedCourses.length === 0) {
      setImportResult({
        success: false,
        message: '没有可导入的课程'
      });
      return;
    }

    try {
      const coursesToImport: Omit<Course, 'id'>[] = parsedCourses.map(parsed => {
        const { frequency, duration } = parseSchedule(parsed.schedule);
        
        return {
          name: parsed.name,
          subject: '计算机科学', // 默认学科
          teacherId: findOrCreateTeacher(parsed.teacherName, '计算机科学'),
          classroomId: findClassroom(parsed.classroom),
          duration,
          frequency,
          studentCount: 32, // 默认学生数
          requirements: {
            consecutiveSlots: duration > 1
          }
        };
      });

      onImportCourses(coursesToImport);
      
      setImportResult({
        success: true,
        message: `成功导入 ${coursesToImport.length} 门课程！`
      });
      
      // 清空输入
      setInputText('');
      setParsedCourses([]);
    } catch (error) {
      setImportResult({
        success: false,
        message: `导入失败: ${error instanceof Error ? error.message : '未知错误'}`
      });
    }
  };

  return (
    <div className="space-y-6">
      <div className="card">
        <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
          <Upload className="h-5 w-5" />
          课程批量导入
        </h3>
        
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              粘贴课程信息 (制表符分隔)
            </label>
            <textarea
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              className="input-field h-32 font-mono text-sm"
              placeholder="请粘贴课程信息，格式如下：
高级算法设计与分析2班	周毅	计算机科学与工程学院（网络空间安全学院）	清水河	32	2	1-8周，星期一第7-8节 星期三第7-8节 (立人楼B406)"
            />
          </div>
          
          <div className="flex gap-2">
            <button
              onClick={handleParse}
              className="btn-secondary flex items-center gap-2"
            >
              <FileText className="h-4 w-4" />
              解析课程
            </button>
            
            {parsedCourses.length > 0 && (
              <button
                onClick={handleImport}
                className="btn-primary flex items-center gap-2"
              >
                <Upload className="h-4 w-4" />
                导入课程
              </button>
            )}
          </div>
        </div>
      </div>

      {/* 解析结果 */}
      {importResult && (
        <div className="card">
          <div className="flex items-center gap-3 mb-3">
            {importResult.success ? (
              <CheckCircle className="h-5 w-5 text-green-500" />
            ) : (
              <AlertCircle className="h-5 w-5 text-red-500" />
            )}
            <h4 className="font-medium">
              {importResult.success ? '操作成功' : '操作失败'}
            </h4>
          </div>
          
          <p className={`mb-3 ${
            importResult.success ? 'text-green-700' : 'text-red-700'
          }`}>
            {importResult.message}
          </p>
          
          {importResult.details && (
            <div className="bg-gray-50 rounded-lg p-3">
              <p className="text-sm font-medium text-gray-700 mb-2">课程列表:</p>
              <ul className="text-sm text-gray-600 space-y-1">
                {importResult.details.map((detail, index) => (
                  <li key={index}>• {detail}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      {/* 解析预览 */}
      {parsedCourses.length > 0 && (
        <div className="card">
          <h4 className="font-medium mb-3">解析预览 ({parsedCourses.length} 门课程)</h4>
          <div className="space-y-2 max-h-60 overflow-y-auto">
            {parsedCourses.map((course, index) => (
              <div key={index} className="bg-gray-50 rounded-lg p-3 text-sm">
                <div className="font-medium text-gray-900">{course.name}</div>
                <div className="text-gray-600 mt-1">
                  教师: {course.teacherName} | 
                  学时: {course.hours} | 
                  学分: {course.credits}
                  {course.classroom && ` | 教室: ${course.classroom}`}
                </div>
                <div className="text-gray-500 text-xs mt-1">
                  {course.schedule}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* 使用说明 */}
      <div className="card bg-blue-50 border-blue-200">
        <h4 className="font-medium text-blue-900 mb-2">使用说明</h4>
        <div className="text-sm text-blue-800 space-y-1">
          <p>• 每行一门课程，使用制表符(Tab)分隔各字段</p>
          <p>• 格式：课程名 → 教师名 → 学院 → 校区 → 学时 → 学分 → 时间安排</p>
          <p>• 支持多个教师，用逗号分隔</p>
          <p>• 时间安排示例：1-8周，星期一第7-8节 星期三第7-8节 (立人楼B406)</p>
          <p>• 教室信息用括号包围，如 (立人楼B406)</p>
        </div>
      </div>
    </div>
  );
};
