import React, { useState } from 'react';
import { Classroom } from '../types';
import { Plus, Edit2, Trash2, Building } from 'lucide-react';

interface ClassroomManagerProps {
  classrooms: Classroom[];
  onAddClassroom: (classroom: Omit<Classroom, 'id'>) => void;
  onUpdateClassroom: (id: string, classroom: Partial<Classroom>) => void;
  onDeleteClassroom: (id: string) => void;
}

const classroomTypes = [
  { value: 'normal', label: 'ÊôÆÈÄöÊïôÂÆ§' },
  { value: 'lab', label: 'ÂÆûÈ™åÂÆ§' },
  { value: 'multimedia', label: 'Â§öÂ™í‰ΩìÊïôÂÆ§' },
  { value: 'gym', label: '‰ΩìËÇ≤È¶Ü' }
] as const;

export const ClassroomManager: React.FC<ClassroomManagerProps> = ({
  classrooms,
  onAddClassroom,
  onUpdateClassroom,
  onDeleteClassroom
}) => {
  const [isAddingClassroom, setIsAddingClassroom] = useState(false);
  const [editingClassroom, setEditingClassroom] = useState<string | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    capacity: '',
    type: 'normal' as Classroom['type'],
    equipment: '',
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const classroomData = {
      name: formData.name,
      capacity: parseInt(formData.capacity),
      type: formData.type,
      equipment: formData.equipment.split(',').map(s => s.trim()).filter(s => s)
    };

    if (editingClassroom) {
      onUpdateClassroom(editingClassroom, classroomData);
      setEditingClassroom(null);
    } else {
      onAddClassroom(classroomData);
      setIsAddingClassroom(false);
    }

    setFormData({ name: '', capacity: '', type: 'normal', equipment: '' });
  };

  const handleEdit = (classroom: Classroom) => {
    setFormData({
      name: classroom.name,
      capacity: classroom.capacity.toString(),
      type: classroom.type,
      equipment: classroom.equipment.join(', ')
    });
    setEditingClassroom(classroom.id);
    setIsAddingClassroom(true);
  };

  const handleCancel = () => {
    setIsAddingClassroom(false);
    setEditingClassroom(null);
    setFormData({ name: '', capacity: '', type: 'normal', equipment: '' });
  };

  const getTypeLabel = (type: Classroom['type']) => {
    return classroomTypes.find(t => t.value === type)?.label || type;
  };

  const getTypeColor = (type: Classroom['type']) => {
    switch (type) {
      case 'lab': return 'bg-green-100 text-green-800';
      case 'multimedia': return 'bg-purple-100 text-purple-800';
      case 'gym': return 'bg-orange-100 text-orange-800';
      default: return 'bg-blue-100 text-blue-800';
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900 flex items-center gap-2">
          <Building className="h-6 w-6" />
          ÊïôÂÆ§ÁÆ°ÁêÜ
        </h2>
        <button
          onClick={() => setIsAddingClassroom(true)}
          className="btn-primary flex items-center gap-2"
        >
          <Plus className="h-4 w-4" />
          Ê∑ªÂä†ÊïôÂÆ§
        </button>
      </div>

      {isAddingClassroom && (
        <div className="card">
          <h3 className="text-lg font-semibold mb-4">
            {editingClassroom ? 'ÁºñËæëÊïôÂÆ§' : 'Ê∑ªÂä†Êñ∞ÊïôÂÆ§'}
          </h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                ÊïôÂÆ§ÂêçÁß∞ *
              </label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="input-field"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                ÂÆπÈáè *
              </label>
              <input
                type="number"
                value={formData.capacity}
                onChange={(e) => setFormData({ ...formData, capacity: e.target.value })}
                className="input-field"
                min="1"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                ÊïôÂÆ§Á±ªÂûã *
              </label>
              <select
                value={formData.type}
                onChange={(e) => setFormData({ ...formData, type: e.target.value as Classroom['type'] })}
                className="input-field"
                required
              >
                {classroomTypes.map((type) => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                ËÆæÂ§á (Áî®ÈÄóÂè∑ÂàÜÈöî)
              </label>
              <input
                type="text"
                value={formData.equipment}
                onChange={(e) => setFormData({ ...formData, equipment: e.target.value })}
                className="input-field"
                placeholder="‰æãÂ¶Ç: ÊäïÂΩ±‰ª™, ÈªëÊùø, Èü≥Âìç"
              />
            </div>
            <div className="flex gap-2">
              <button type="submit" className="btn-primary">
                {editingClassroom ? 'Êõ¥Êñ∞' : 'Ê∑ªÂä†'}
              </button>
              <button type="button" onClick={handleCancel} className="btn-secondary">
                ÂèñÊ∂à
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {classrooms.map((classroom) => (
          <div key={classroom.id} className="card">
            <div className="flex justify-between items-start mb-3">
              <h3 className="text-lg font-semibold text-gray-900">{classroom.name}</h3>
              <div className="flex gap-1">
                <button
                  onClick={() => handleEdit(classroom)}
                  className="p-1 text-gray-500 hover:text-primary-600"
                >
                  <Edit2 className="h-4 w-4" />
                </button>
                <button
                  onClick={() => onDeleteClassroom(classroom.id)}
                  className="p-1 text-gray-500 hover:text-red-600"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            <div className="space-y-2 mb-3">
              <p className="text-sm text-gray-600">üë• ÂÆπÈáè: {classroom.capacity}‰∫∫</p>
              <span className={`inline-block px-2 py-1 text-xs rounded-full ${getTypeColor(classroom.type)}`}>
                {getTypeLabel(classroom.type)}
              </span>
            </div>
            
            {classroom.equipment.length > 0 && (
              <div className="flex flex-wrap gap-1">
                {classroom.equipment.map((equipment, index) => (
                  <span
                    key={index}
                    className="px-2 py-1 bg-gray-100 text-gray-700 text-xs rounded"
                  >
                    {equipment}
                  </span>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>

      {classrooms.length === 0 && (
        <div className="text-center py-12 text-gray-500">
          <Building className="h-12 w-12 mx-auto mb-4 opacity-50" />
          <p>ÊöÇÊó†ÊïôÂÆ§‰ø°ÊÅØÔºåÁÇπÂáª‰∏äÊñπÊåâÈíÆÊ∑ªÂä†ÊïôÂÆ§</p>
        </div>
      )}
    </div>
  );
};
