import { 
  Course, 
  Teacher, 
  TimeSlot, 
  ScheduleItem, 
  Conflict 
} from '../types';

export interface SchedulingResult {
  schedule: ScheduleItem[];
  conflicts: Conflict[];
  success: boolean;
  message: string;
}

export class SchedulingAlgorithm {
  private courses: Course[];
  private teachers: Teacher[];
  private timeSlots: TimeSlot[];
  private schedule: ScheduleItem[];

  constructor(
    courses: Course[],
    teachers: Teacher[],
    timeSlots: TimeSlot[]
  ) {
    this.courses = courses;
    this.teachers = teachers;
    this.timeSlots = timeSlots.sort((a, b) => a.order - b.order);
    this.schedule = [];
  }

  // 主排课方法
  public generateSchedule(): SchedulingResult {
    this.schedule = [];
    const conflicts: Conflict[] = [];

    try {
      // 根据每个课程的固定时间段生成课表
      for (const course of this.courses) {
        this.scheduleForCourse(course);
      }

      // 检查冲突
      const detectedConflicts = this.detectConflicts();
      conflicts.push(...detectedConflicts);

      return {
        schedule: this.schedule,
        conflicts,
        success: conflicts.length === 0,
        message: conflicts.length === 0 
          ? '课表生成成功！' 
          : `课表生成完成，但存在 ${conflicts.length} 个时间冲突`
      };
    } catch (error) {
      return {
        schedule: [],
        conflicts,
        success: false,
        message: `生成失败: ${error instanceof Error ? error.message : '未知错误'}`
      };
    }
  }

  // 为单个课程排课
  private scheduleForCourse(course: Course): void {
    // 根据课程的固定时间段生成课表条目
    for (const fixedSlot of course.fixedTimeSlots) {
      for (const timeSlotId of fixedSlot.timeSlotIds) {
        const scheduleItem: ScheduleItem = {
          id: `${course.id}-${fixedSlot.dayOfWeek}-${timeSlotId}`,
          courseId: course.id,
          teacherId: course.teacherId,
          timeSlotId: timeSlotId,
          dayOfWeek: fixedSlot.dayOfWeek,
          location: course.location
        };
        
        this.schedule.push(scheduleItem);
      }
    }
  }

  // 检测冲突
  private detectConflicts(): Conflict[] {
    const conflicts: Conflict[] = [];

    // 检查时间冲突 - 同一时间段有多个课程
    const timeConflicts = this.detectTimeConflicts();
    conflicts.push(...timeConflicts);

    return conflicts;
  }

  // 获取可用时间段
  private getAvailableTimeSlots(course: Course, classItem: Class, teacher: Teacher) {
    const availableSlots: { dayOfWeek: number; timeSlotId: string }[] = [];

    for (let day = 1; day <= 5; day++) { // 周一到周五
      for (const timeSlot of this.timeSlots) {
        // 检查教师是否可用
        if (teacher.unavailableSlots.includes(timeSlot.id)) continue;

        // 检查是否在禁止时间段
        if (this.constraints.forbiddenSlots.some(
          fs => fs.dayOfWeek === day && fs.timeSlotId === timeSlot.id
        )) continue;

        // 检查教师冲突
        if (this.hasTeacherConflict(teacher.id, day, timeSlot.id)) continue;

        // 检查班级冲突
        if (this.hasClassConflict(classItem.id, day, timeSlot.id)) continue;

        // 检查连续课程要求
        if (course.duration > 1 && course.requirements.consecutiveSlots) {
          if (this.canScheduleConsecutiveSlots(course, day, timeSlot.id, classItem.id, teacher.id)) {
            availableSlots.push({ dayOfWeek: day, timeSlotId: timeSlot.id });
          }
        } else {
          availableSlots.push({ dayOfWeek: day, timeSlotId: timeSlot.id });
        }
      }
    }

    return availableSlots;
  }

  // 计算时间段评分
  private calculateSlotScore(
    slot: { dayOfWeek: number; timeSlotId: string },
    course: Course,
    classItem: Class,
    teacher: Teacher
  ): number {
    let score = 100;

    // 避免午休时间
    if (this.constraints.lunchBreakSlots.includes(slot.timeSlotId)) {
      score -= 20;
    }

    // 优先上午时间
    const timeSlot = this.timeSlots.find(ts => ts.id === slot.timeSlotId);
    if (timeSlot && timeSlot.order <= 4) {
      score += 10;
    }

    // 避免周五下午
    if (slot.dayOfWeek === 5 && timeSlot && timeSlot.order > 4) {
      score -= 15;
    }

    // 检查班级当天已有课程数量
    const dailyClasses = this.schedule.filter(
      s => s.classId === classItem.id && s.dayOfWeek === slot.dayOfWeek
    ).length;
    
    if (dailyClasses >= this.constraints.maxDailyHours) {
      score -= 50;
    }

    return score;
  }

  // 分配教室
  private assignClassroom(course: Course, dayOfWeek: number, timeSlotId: string): Classroom | null {
    // 如果课程指定了教室
    if (course.classroomId) {
      const classroom = this.classrooms.find(c => c.id === course.classroomId);
      if (classroom && !this.hasClassroomConflict(classroom.id, dayOfWeek, timeSlotId)) {
        return classroom;
      }
    }

    // 根据要求筛选教室
    let availableClassrooms = this.classrooms.filter(classroom => {
      // 检查容量
      if (classroom.capacity < course.studentCount) return false;

      // 检查类型要求
      if (course.requirements.classroomType && 
          classroom.type !== course.requirements.classroomType) return false;

      // 检查设备要求
      if (course.requirements.equipment) {
        const hasAllEquipment = course.requirements.equipment.every(
          equipment => classroom.equipment.includes(equipment)
        );
        if (!hasAllEquipment) return false;
      }

      // 检查时间冲突
      if (this.hasClassroomConflict(classroom.id, dayOfWeek, timeSlotId)) return false;

      return true;
    });

    // 按容量排序，优先选择容量适中的教室
    availableClassrooms.sort((a, b) => {
      const aWaste = a.capacity - course.studentCount;
      const bWaste = b.capacity - course.studentCount;
      return aWaste - bWaste;
    });

    return availableClassrooms[0] || null;
  }

  // 检查教师冲突
  private hasTeacherConflict(teacherId: string, dayOfWeek: number, timeSlotId: string): boolean {
    return this.schedule.some(
      item => item.teacherId === teacherId && 
               item.dayOfWeek === dayOfWeek && 
               item.timeSlotId === timeSlotId
    );
  }

  // 检查班级冲突
  private hasClassConflict(classId: string, dayOfWeek: number, timeSlotId: string): boolean {
    return this.schedule.some(
      item => item.classId === classId && 
               item.dayOfWeek === dayOfWeek && 
               item.timeSlotId === timeSlotId
    );
  }

  // 检查教室冲突
  private hasClassroomConflict(classroomId: string, dayOfWeek: number, timeSlotId: string): boolean {
    return this.schedule.some(
      item => item.classroomId === classroomId && 
               item.dayOfWeek === dayOfWeek && 
               item.timeSlotId === timeSlotId
    );
  }

  // 检查是否可以安排连续时间段
  private canScheduleConsecutiveSlots(
    course: Course, 
    dayOfWeek: number, 
    startTimeSlotId: string,
    classId: string,
    teacherId: string
  ): boolean {
    const startSlot = this.timeSlots.find(ts => ts.id === startTimeSlotId);
    if (!startSlot) return false;

    const startIndex = this.timeSlots.indexOf(startSlot);
    
    // 检查是否有足够的连续时间段
    for (let i = 0; i < course.duration; i++) {
      const slotIndex = startIndex + i;
      if (slotIndex >= this.timeSlots.length) return false;

      const timeSlot = this.timeSlots[slotIndex];
      
      // 检查教师和班级冲突
      if (this.hasTeacherConflict(teacherId, dayOfWeek, timeSlot.id) ||
          this.hasClassConflict(classId, dayOfWeek, timeSlot.id)) {
        return false;
      }
    }

    return true;
  }

  // 检测冲突
  private detectConflicts(): Conflict[] {
    const conflicts: Conflict[] = [];

    // 检查教师冲突
    const teacherConflicts = this.detectTeacherConflicts();
    conflicts.push(...teacherConflicts);

    // 检查教室冲突
    const classroomConflicts = this.detectClassroomConflicts();
    conflicts.push(...classroomConflicts);

    // 检查班级冲突
    const classConflicts = this.detectClassConflicts();
    conflicts.push(...classConflicts);

    return conflicts;
  }

  private detectTeacherConflicts(): Conflict[] {
    const conflicts: Conflict[] = [];
    const teacherSchedule = new Map<string, ScheduleItem[]>();

    // 按教师分组
    this.schedule.forEach(item => {
      if (!teacherSchedule.has(item.teacherId)) {
        teacherSchedule.set(item.teacherId, []);
      }
      teacherSchedule.get(item.teacherId)!.push(item);
    });

    // 检查每个教师的时间冲突
    teacherSchedule.forEach((items, teacherId) => {
      const timeMap = new Map<string, ScheduleItem[]>();
      
      items.forEach(item => {
        const key = `${item.dayOfWeek}-${item.timeSlotId}`;
        if (!timeMap.has(key)) {
          timeMap.set(key, []);
        }
        timeMap.get(key)!.push(item);
      });

      timeMap.forEach(conflictItems => {
        if (conflictItems.length > 1) {
          const teacher = this.teachers.find(t => t.id === teacherId);
          conflicts.push({
            type: 'teacher',
            message: `教师 ${teacher?.name || teacherId} 在同一时间段有多个课程安排`,
            items: conflictItems
          });
        }
      });
    });

    return conflicts;
  }

  private detectClassroomConflicts(): Conflict[] {
    const conflicts: Conflict[] = [];
    const classroomSchedule = new Map<string, ScheduleItem[]>();

    // 按教室分组
    this.schedule.forEach(item => {
      if (!classroomSchedule.has(item.classroomId)) {
        classroomSchedule.set(item.classroomId, []);
      }
      classroomSchedule.get(item.classroomId)!.push(item);
    });

    // 检查每个教室的时间冲突
    classroomSchedule.forEach((items, classroomId) => {
      const timeMap = new Map<string, ScheduleItem[]>();
      
      items.forEach(item => {
        const key = `${item.dayOfWeek}-${item.timeSlotId}`;
        if (!timeMap.has(key)) {
          timeMap.set(key, []);
        }
        timeMap.get(key)!.push(item);
      });

      timeMap.forEach(conflictItems => {
        if (conflictItems.length > 1) {
          const classroom = this.classrooms.find(c => c.id === classroomId);
          conflicts.push({
            type: 'classroom',
            message: `教室 ${classroom?.name || classroomId} 在同一时间段被多个课程占用`,
            items: conflictItems
          });
        }
      });
    });

    return conflicts;
  }

  private detectClassConflicts(): Conflict[] {
    const conflicts: Conflict[] = [];
    const classSchedule = new Map<string, ScheduleItem[]>();

    // 按班级分组
    this.schedule.forEach(item => {
      if (!classSchedule.has(item.classId)) {
        classSchedule.set(item.classId, []);
      }
      classSchedule.get(item.classId)!.push(item);
    });

    // 检查每个班级的时间冲突
    classSchedule.forEach((items, classId) => {
      const timeMap = new Map<string, ScheduleItem[]>();
      
      items.forEach(item => {
        const key = `${item.dayOfWeek}-${item.timeSlotId}`;
        if (!timeMap.has(key)) {
          timeMap.set(key, []);
        }
        timeMap.get(key)!.push(item);
      });

      timeMap.forEach(conflictItems => {
        if (conflictItems.length > 1) {
          const classItem = this.classes.find(c => c.id === classId);
          conflicts.push({
            type: 'class',
            message: `班级 ${classItem?.name || classId} 在同一时间段有多个课程安排`,
            items: conflictItems
          });
        }
      });
    });

    return conflicts;
  }
}
